    //if [], run once when row loads , and dont run again
    async function fetchData() {
        const request = await axios.get(fetchUrl);
    }
    fetchData();
  }, []);

  return (
    <div>
      <h2>{title}</h2>
      {/* title */}
      {/* container -> posters */}
    </div>
  );
};

export default Row;
